let checkbox=document.querySelector('input[name=theme_switch]');window.matchMedia('(prefers-color-scheme: dark)').matches?(document.documentElement.setAttribute('data-theme','dark'),checkbox.checked=!0):(document.documentElement.setAttribute('data-theme','light'),checkbox.checked=!1),checkbox.addEventListener('change',a=>{document.documentElement.setAttribute('data-theme',a.target.checked?'dark':'light')})