let checkbox=document.querySelector("input[name=theme_switch]");window.matchMedia("(prefers-color-scheme: dark)").matches?(document.documentElement.setAttribute("data-theme","dark"),checkbox.checked=!0):(document.documentElement.setAttribute("data-theme","light"),checkbox.checked=!1),checkbox.addEventListener("change",e=>{document.documentElement.setAttribute("data-theme",e.target.checked?"dark":"light")})